import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import Axios  from 'axios';

function App() {
  const[data, setData] =useState([])
  const getNews =() =>{
    Axios.get("https://newsapi.org/v2/top-headlines?country=in&apiKey=da5e11e4f5c14c1aac34f67d00097d8c&q=india")
    .then((response)=>{
      console.log(response)
      setData(response.data.articles)
    })
  }
  return (
    <>
    <center>
    <div>
      <button className='App' onClick={getNews}>GET NEWS</button>
    </div>
    </center>


<div className='container'>
  <div className='row'>
    {
      data.map((value)=>{
        return(
          <div className='col'>
            <div className="card" style={{width:"18rem"}}>
              <img src={value.urlToImage} className="card-img-top" alt="..."/>
              <div className="card-body">
                <h5 className="card-title">{value.title}</h5>
                <p className="card-text">{value.description}</p>
                <a className="card-link">{value.url}</a>
                <a className='btn btn-light'>{value.publishedAt}</a>
              </div>

            </div>
          </div>
        );
      })
    }
  </div>
</div>
</>
  );
}

export default App;
